    private static Guid GetIdFromUrl(string url)
    {
        var uri = new Uri(url);
        var path = uri.LocalPath;
        var slashIndex = path.LastIndexOf('/');
        var id = path[(slashIndex + 1)..];
        return Guid.Parse(id);
    }
